            items.Add(new Items("Item217", "Леший", "Артефакт", "(Скорость +40, Быстрота) Древние волшебные чары Лешего заставляют его практически лететь в горло врага.  Никто не знает, где и когда был создан Леший, но участие фей при его изготовлении очевидно.", 20000, 0, 0, 0, Items.ITEM_EQUIP_TYPE.EQUIP_SINGLE_HANDED, 0, 0, 0, 0, 0, 0, 0, 0, 0));
            items.Add(new Items("Item218", "Железное Перо", "Артефакт", "(Сила +40, 6-15 очков урона от Грома) Необыкновенно легкий двуручный меч Железное Перо  повышает Силу своего владельца и делает обращение с тяжелым оружием более легким.  Это сочетание почти позволяет использовать меч одной рукой - единственной проблемой остается только большой размер клинка", 0, 0, 0, 0, Items.ITEM_EQUIP_TYPE.EQUIP_SINGLE_HANDED, 0, 0, 0, 0, 0, 0, 0, 0, 0));
            items.Add(new Items("Item219", "Уоллес", "Артефакт", "(Навык Оружейника +10, Сила духа +40) Уоллес каким-то образом нашел дорогу в руки мятежников и подстрекателей.  Это оружие улучшает как способности, так и уверенность владельцев в собственных силах, что часто приводит их к рискованным делам, которых можно было бы избежать.  Хотя ни одна из амбиций его прежних владельцев так и не была реализована, еще никто никогда не отказывался взять Уоллес на вооружение, услышав его сладкозвучную песню.", 0, 0, 0, 0, Items.ITEM_EQUIP_TYPE.EQUIP_SINGLE_HANDED, 0, 0, 0, 0, 0, 0, 0, 0, 0));
            items.Add(new Items("Item220", "Корсар", "Артефакт", "(Навык воровства +5, навык нейтрализации ловушек +5, Удача +40) Корсар - один из прекраснейших видов оружия, когда-либо выпускавшихся в регнанских оружейных мастерских.  Всегда верные природе, регнанские кузнецы изготовили оружие, которое помогало бы имперским усилиям по сбору налогов в открытых морях и прибрежных городах.  Еще ни разу Регна не создала оружия, которое бы не стремилось бы к ее эгоистическим целям за счет других.", 0, 0, 0, 0, Items.ITEM_EQUIP_TYPE.EQUIP_SINGLE_HANDED, 0, 0, 0, 0, 0, 0, 0, 0, 0));
            items.Add(new Items("Item221", "Доспехи Правителя", "Артефакт", "(Половина урона от метательного оружия, +10 ко всем показателям) Принадлежавшие колониальному Правителю Падишу во время Забвения, эти фантастические доспехи побывали во многих кладах.  Подобно многим творениям Небесной Кузницы, кольчугу почти не возможно повредить и ее подобия больше не изготовить ни в одной кузнице страны.", 0, 0, 0, 0, Items.ITEM_EQUIP_TYPE.EQUIP_SINGLE_HANDED, 0, 0, 0, 0, 0, 0, 0, 0, 0));
            items.Add(new Items("Item222", "Йоруба", "Артефакт", "(Иммунитет к болезням, безумию, параличу, яду, сну и окаменению, Выносливость +25) Названные в честь своего владельца и создателя, Йоруба - это чрезвычайно прочные, устойчивые к магии латы.  Из-за высокой дороговизны брони латы были сделаны сборными, и это заметно.  Различные части лат сделаны из различных материалов: самые дорогие - в нагруднике.  В нагруднике два мелких отверстия, которые первый собственник так никогда и не заделал, возможно, по сентиментальным причинам.", 0, 0, 0, 0, Items.ITEM_EQUIP_TYPE.EQUIP_SINGLE_HANDED, 0, 0, 0, 0, 0, 0, 0, 0, 0));
